mapper.select:
select * from repository_c

	TENANT_ID = #{tenantId} 
	 AND TYPE = #{type}

mapper.update:
update repository_c
REPOSITORY_NO = #{repositoryNo}, REPOSITORY_NAME = #{name}, SHORT_NAME = #{shortName}, TYPE = #{type}, CREATE_TIME = #{createTime}, LOCATION = #{location}, AREA = #{area}, MEMO = #{memo},
where repository_id = #{id}

mapper.delete:
delete from repository_c 
where repository_id in

	#{id}
